package assignment3;

public class booleann {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean bool1 = true; // declare and assign value to first variable
		boolean bool2 = false; // declare and assign value to second variable
		boolean result = bool1 && bool2; // apply the logical AND operator to the two variables and assign result to a new variable
		System.out.println("The result of the logical AND operator applied to " + bool1 + " and " + bool2 + " is " + result); // print the result


	}

}
