package assignment3;

public class substring {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String myString = "Hello, world!"; // declare and assign a value to the string variable
		String substring = myString.substring(0, 5); // call the substring() method on the string and assign the result to a new variable
		System.out.println("The first 5 characters of the string \"" + myString + "\" are: " + substring); // print the result

		

	}

}
